function drag(evt)
{
	if ( down == 1 ){
		var target = evt.getTarget();
		var LCTM = target.getCTM();						
		//If transformation matrix on target is non-zero, getCTM must have given back the full transformation
		if(LCTM.e != 0 && LCTM.f != 0) ASV6=true;						
		if(ASV6)
		{
			matrix=evt.currentTarget.parentNode.getScreenCTM().multiply(evt.target.parentNode.getTransformToElement(evt.currentTarget.parentNode));
			p2valx= matrix.inverse().a*evt.screenX+matrix.inverse().c*evt.screenY+matrix.inverse().e;						
		}
		else					
		{
			CTM = getTransformToElement(target);
			iCTM = CTM.inverse();
			p1.x = (evt.clientX - doct.currentTranslate.x)/doct.currentScale;
			p1.y = (evt.clientY - doct.currentTranslate.y)/doct.currentScale;
			p2 = p1.matrixTransform(iCTM);
			p2valx=p2.x;
			p2valy=p2.y;
		};

		scp2x=controlorigin*1+p2valx*controlscale;
		controltext.firstChild.setData(scp2x );
		controlslider.setAttribute("cx", p2valx);
		update();
	}
}
