	objtr5.setAttribute('transform', 'rotate(' + curslider7  + ',' + curslider8 + ',' + curslider8 + ')' );				
}

function getSVGDocument(node)
{
	if( node.getNodeType() != 9 )  // if not DOCUMENT_NODE
		return node.getOwnerDocument();
	else
		return node;
}
function getTransformToElement(node) {
	// From Kevlindev
	var LCTM = node.getCTM();
		while ( ( node = node.parentNode ) != svgDocument ) {
		LCTM = node.getCTM().multiply(LCTM);
	}
	return LCTM;
}
